import React from "react"

// HACK: used any here
interface RunSimulationProps {
	run: boolean,
	setRun: (run: boolean) => void, // waving the white flag
	runGame: (grid: boolean[][]) => void,
	runRef: any;
	iteration: number,
	setIteration: (iteration: number) => void,
	grid: boolean[][],
}

const RunSimulation: React.FC<RunSimulationProps> = ({ 
	run, 
	setRun, 
	runRef, 
	runGame, 
	iteration, 
	setIteration,
	grid,
}) => {
	const name = run ? "Stop" : "Run";

	const handleRun = () => {
		setRun(!run);
		if (!run) {
			runRef.current = true;
			runGame(grid);
		}
	}

	return (
		<div>

			<button className="control-button" onClick={() => handleRun()}>
				{name}
			</button>
		</div>

	);
}

export default RunSimulation;
