	setSpeed: any, // white flag
}

const SelectSpeed: React.FC<SelectSpeedProps> = ({ speed, setSpeed }) => {
	const [count, setCount] = useState(0);

	const handleSpeedEvent = () => {
		setCount(count + 1);
		switch (count % 4) {
			case 0:
				setSpeed(0);
				break;
			case 1:
				setSpeed(1);
				break;
			case 2:
				setSpeed(2);
				break;
			case 3:
				setSpeed(3);
				break;
			default:
				break;
		}
	}

	return (
		<div>
			<button className="control-button">
				{speed}
			</button>
		</div>
	);
}

export default SelectSpeed;
