import React, { useState } from 'react';
import BoardButton from './Tile';
import "./styles.css";


interface BoardProps {
	grid: boolean[][],
	setGrid: (grid: boolean[][]) => void,
	iteration: number,
	cellsAlive: number,
}

const Board: React.FC<BoardProps> = ({ grid, setGrid, iteration, cellsAlive }) => {

	const handleTileEvent = (i: number, j: number) => {
		let gridCopy: boolean[][] = grid.map(arr => arr.slice());
		gridCopy[i][j] = grid[i][j] ? false : true;
		setGrid(gridCopy);
	}

	// TODO: Add Borders around Iteration and CellsAlive header tags
	return (
		<div className="board-container">
			<h1 style={{border: "1px solid black"}}>
				Iteration: {iteration}
			</h1>
			<div
				style={{
					display: "grid",
					gridTemplateColumns: `repeat(${grid[0].length}, 20px`,
					justifyContent: "center"
				}}
			>
				{ grid.map( (row, i) => {
					return (
						grid[i].map( (val, j) => {
							return (
								<BoardButton 
									keyID={`${i}-${j}`} 
									tileName={grid[i][j] ? "tile-on" : "tile-off"}	
									onClick={() => handleTileEvent(i, j)} 
								/>
							)
						})
					)
				}) }
			</div>
			<h1>
				Cells Alive: {cellsAlive}
			</h1>
		</div>
	)
}

export default Board;
