import React, { useState } from "react";
import Speed from "../../App";

interface SelectSpeedProps {
	speed: typeof Speed,
	// setSpeed: (speed: typeof Speed) => void, 
	setSpeed: React.Dispatch<React.SetStateAction<typeof Speed>>,
}

const SelectSpeed: React.FC<SelectSpeedProps> = ({ speed, setSpeed }) => {
	const [count, setCount] = useState(0);

	const handleSpeedEvent = () => {
		if (count == 3) {
			setCount(0);
		} else {
			setCount(count + 1);
		}

		switch (count % 4) {
			case 0:
				setSpeed("Slow");
				break;
			case 1:
				setSpeed("Medium");
				break;
			case 2:
				setSpeed("Fast");
				break;
			case 3:
				setSpeed("Brrr");
				break;
			default:
				break;
		}
	}

	return (
		<div>
			<button className="control-button" onClick={() => handleSpeedEvent()}>
				{speed}
			</button>
		</div>
	);
}

export default SelectSpeed;
