import { useCallback, useRef, useEffect, useState } from "react";

import Board from "./components/Board"
import "./App.css";
import RunSimulation from "./components/Controls/RunSimulation";
import SelectSpeed from "./components/Controls/SelectSpeed";
import GraphButton from "./components/Controls/GraphButton";
import ClearBoard from "./components/Controls/ClearBoard";
import { getNextState, NextStateType } from "./utils";

type Speed = "Slow" | "Medium" | "Fast" | "Brrr"; 

const App: React.FC = () => {
	const [run, setRun] = useState<boolean>(false);
	const [speed, setSpeed] = useState<Speed>("Slow");
	const [showGraph, setShowGraph] = useState<boolean>(false);
	const [iteration, setIteration] = useState<number>(0);
	const [cellsAlive, setCellsAlive] = useState<number>(0);
	const [grid, setGrid] = useState<boolean[][]>(Array(50).fill(Array(75).fill(0)))

	const runRef = useRef<boolean>(run);
	runRef.current = run

	const runGame = useCallback(() => {
		if (!runRef.current) {
			return;
		}
		const { nextGrid, nextCellsAlive }: NextStateType = getNextState(grid);
		console.log(nextCellUAlive);
		// setGrid((g) => getNextState(g));
		setGrid(() => nextGrid);
		setCellsAlive(() => nextCellsAlive);
		setIteration( (i) => i + 1); // why does this update everytime but setIteration(i + 1) does not?
		// setGrid(getNextState(grid));
		// setIteration(iteration + 1);
		setTimeout(runGame, 101);
	}, []);

	return (
		<div>
			<header className="basic-header">
				<h1>
					Conway's Game of Life
				</h1>
			</header>
			<Board 
				grid={grid} 
				setGrid={setGrid} 
				iteration={iteration} 
				cellsAlive={cellsAlive}
				setCellsAlive={setCellsAlive}
			/>
			<header className="basic-header">
				<h1>
					Controls	
				</h1>
			</header>
			<div className="controls-container">
				<RunSimulation 
					run={run} 
					setRun={setRun} 
					runRef={runRef} 
					runGame={runGame} 
					iteration={iteration} 
					setIteration={setIteration}
				/>
				<ClearBoard setGrid={setGrid} setIteration={setIteration} setCellsAlive={setCellsAlive}/>
				<SelectSpeed />
				<GraphButton />
			</div>
		</div>
	)
}


export default App;
