import React, { useState } from "react";
import Speed from "../../types/Speed"

interface SelectSpeedProps {
	speed: Speed,
	setSpeed: (speed: Speed) => void, 
	speedRef: any,
}

const SelectSpeed: React.FC<SelectSpeedProps> = ({ speed, setSpeed, speedRef }) => {
	const [count, setCount] = useState(0);

	const handleSpeedEvent = () => {
		if (count == 3) {
			setCount(0);
		} else {
			setCount(count + 1);
		}

		switch (count % 4) {
			case 0:
				setSpeed("Slow");
				speedRef.currrent = "Slow";
				break;
			case 1:
				setSpeed("Medium");
				speedRef.currrent = "Medium";
				break;
			case 2:
				setSpeed("Fast");
				speedRef.currrent = "Fast";
				break;
			case 3:
				setSpeed("Brrr");
				speedRef.currrent = "Brrr";
				break;
			default:
				break;
		}
	}

	return (
		<div>
			<button ref={speedRef} className="control-button" onClick={() => handleSpeedEvent()}>
				{speed}
			</button>
		</div>
	);
}

export default SelectSpeed;
